

import { FileEmitter, FileEmitOptions, MethodEmitOptions, NamespaceEmitOptions } from 'fluffy-spoon.javascript.csharp-to-typescript-generator';
export class TsGenerator {
    private _content: string;
    constructor(content: string) {
        this._content = content;
    }
    GetTypescriptCode(): string {
        var emitter = new FileEmitter(this._content);
        var options = <FileEmitOptions>{};
        options = {
            namespaceEmitOptions: <NamespaceEmitOptions>{
                skip: true
            }
        };
        var typescriptCode = emitter.emitFile(options);
        return typescriptCode;
    }
}